mapper.select:
SELECT *
FROM USERS
WHERE USER_ID = #{userId}
  AND USER_PWD = #{userPwd}

mapper.update:
UPDATE USERS
SET USER_NAME = #{userName},
    USER_PHONE = #{userPhone},
    USER_EMAIL = #{userEmail}
WHERE USER_NO = #{userNo}

mapper.select:
SELECT USER_ID
FROM USERS
WHERE USER_NAME = #{userName}
  AND USER_PHONE = #{userPhone}

SELECT USER_PWD
FROM USERS
WHERE USER_ID = #{userId}
  AND USER_NAME = #{userName}
  AND USER_PHONE = #{userPhone}
